from selenium import webdriver
from selenium.common.exceptions import TimeoutException
from selenium.webdriver.chrome.options import Options
import warnings
from time import sleep

#//*[@id="easyindex-index"]/li[1]/div/a

warnings.filterwarnings("ignore", category=DeprecationWarning)

# url for site
URL = 'https://sarahscucinabella.com/index/recipes/quick-and-easy-recipes/'

def connect(url):
  """Initializes and returns chrome driver"""
  # create chrome driver
  options = Options()
  # no graphics
  #options.add_argument('headless')
  # replit things
  options.add_argument('--no-sandbox')
  options.add_argument('--disable-dev-shm-usage')
  # reduces log messages
  options.add_argument('--log-level=3')
    
  driver = webdriver.Chrome(options = options)
  # try except to prevent timeout, would kick the scraper
  try:
    driver.get(url)
    sleep(5)
  except TimeoutException:
    print('new connection try')
    driver.get(url)
    sleep(5)

  return driver
    
driver = connect(URL)
